package main

import "fmt"

type Product struct {
	name string
	sales float64
}

func main() {
	products := []Product{
		{"Product A", 1500.50},
		{"Product B", 2300.75},
		{"Product C", 1800.25},
	}

	totalSales := 0.0
	for _, product := range products {
		totalSales += product.sales
		fmt.Printf("%s Sales: $%.2f\n", product.name, product.sales)
	}

	averageSale := totalSales / float64(len(products))

	fmt.Printf("Total Sales: $%.2f\n", totalSales)
	fmt.Printf("Average Sale: $%.2f\n", averageSale)
}
