package main

import (
	"fmt"
	"strconv"
)

func main() {
	var quantity int
	var price, total, discount, discountedTotal float64

	fmt.Print("Enter quantity: ")
	fmt.Scanln(&quantity)

	fmt.Print("Enter price: ")
	fmt.Scanln(&price)

	total = float64(quantity) * price

	if total > 1000 {
		discount = total * 0.1
	} else {
		discount = total * 0.05
	}

	discountedTotal = total - discount

	fmt.Printf("Total: $%.2f\n", total)
	fmt.Printf("Discount: $%.2f\n", discount)
	fmt.Printf("Discounted Total: $%.2f\n", discountedTotal)
}
